import regex as re
def hanonly(input):
	"This uses Regex Unicode class of Chinese characters in order to remove everything else"
	pattern = re.compile(r'([^\p{IsHan}])', re.UNICODE)
	input = pattern.sub('', input)
	return input

def hanwithpunctuation(input):
	"This uses Regex Unicode class of Chinese characters and punctuation in order to remove everything else"
	pattern = re.compile(r'([^\p{InCJK_Symbols_and_Punctuation}\p{IsHan}，])', re.UNICODE)
	input = pattern.sub('', input)
	return input

